const getScripts = require("@ui5/webcomponents-tools/components-package/nps.js"); //eslint-disable-line

const options = {
  port: 8080,
  typescript: true,
};

const scripts = getScripts(options);

// Change baseline Vite dev server config to not open app in browser since we use Storybook
const customWatchBundle = scripts.watch.bundle.split(' ');
customWatchBundle[1] = './config/dev-server.js';
scripts.watch.bundle = customWatchBundle.join(' ');

module.exports = {
  scripts,
};
