import UI5Element from "@ui5/webcomponents-base/dist/UI5Element.js";
import customElement from "@ui5/webcomponents-base/dist/decorators/customElement.js";
import property from "@ui5/webcomponents-base/dist/decorators/property.js";
import slot from "@ui5/webcomponents-base/dist/decorators/slot.js";
import litRender from "@ui5/webcomponents-base/dist/renderer/LitRenderer.js";

// Template
import QuoteTemplate from "./generated/templates/QuoteTemplate.lit.js";

// Styles
import QuoteCss from "./generated/themes/Quote.css.js";

enum SizeType {
  small = "small",
  large = "large"
}

/**
 * @class
 *
 * <h3 class="comment-api-title">Overview</h3>
 *
 * The <code>udex-quote</code> component is a demo component that displays some text.
 *
 * @constructor
 * @alias modal.components.Quote
 * @extends sap.ui.webc.base.UI5Element
 * @tagname udex-quote
 * @public
 */
@customElement({
  tag: "udex-quote",
  renderer: litRender,
  styles: QuoteCss,
  template: QuoteTemplate,
})
class Quote extends UI5Element {
  /**
    * Defines the text content of the Quote.
    * @type {HTMLElement[]}
    * @name sap.ui.webc.main.Quote.prototype.default
    * @slot content
    * @public
    */
  @slot({ type: HTMLElement, "default": true })
    content!: Array<HTMLElement>;

  /**
    * Defines the link in the Quote
    * @type {HTMLElement[]}
    * @name udex.components.Quote.prototype.link
    * @slot
    * @public
  */
  @slot()
    link!: Array<HTMLElement>;

  /**
    * Defines author's name and company
    * @public
    * @type {HTMLElement[]}
    * @name udex.components.Quote.prototype.author
    * @slot
  */
  @slot()
    author!: Array<HTMLElement>;

  /**
    * Defines image near quote text
    * @public
    * @type {HTMLElement[]}
    * @name udex.components.Quote.prototype.image
    * @slot
  */
  @slot()
    image!: Array<HTMLElement>;

  /**
    * Defines image (Avatar or company logo) size
    * @public
    * @type {SizeType}
    * @name udex.components.Quote.prototype.imageSize
    * @defaultvalue "large"
  */
  @property({ type: SizeType, defaultValue: "large" })
    imageSize!: SizeType;

  /**
    * Defines text size
    * @public
    * @type {SizeType}
    * @name udex.components.Quote.prototype.textSize
    * @defaultvalue "large"
  */
  @property({ type: SizeType, defaultValue: "large" })
    textSize!: SizeType;
}

Quote.define();

export default Quote;
